mapper.select:
SELECT id, model_option_id AS modelOptionId, order_id AS orderId, order_quantity AS orderQuantity, order_price AS orderPrice
FROM orderdetails
WHERE order_id = #{orderId}

SELECT od.id AS od_id, od.order_id, od.model_option_id, od.order_quantity, od.order_price,
mo.id AS mo_id, mo.optional_model_price, mo.color, mo.ram, mo.size
FROM orderdetails od JOIN model_options mo ON od.model_option_id = mo.id WHERE od.order_id = #{orderId}
